@Name("simpleAction")
public class SimpleAction {

    @Logger private Log log;

    @In
    FacesMessages facesMessages;

    private String value;

    //seam-gen method
    public String hello()
    {
        //implement your business logic here
        log.info("simpleAction.echo() action called with: #{simpleAction.value}");
        facesMessages.add("echo #{simpleAction.value}");
        return "success";
    }

    //add additional action methods

    @Length(max=10)
    public String getValue()
    {
        return value;
    }

    public void setValue(String value)
    {
        this.value = value;
    }

}

@NotNull
@Pattern(regex="^[a-zA-Z.-]+ [a-zA-Z.-]+",
      message="Need a firstname and a lastname")
public String getValue()
{
        return value;
}

@Name("simpleAction")
public class SimpleAction {

    private boolean convertToCap;
    
    public boolean getConvertToCap () { return convertToCap; }
    public void setConvertToCap (boolean b) { convertToCap = b; }
    
    public String hello()
    {
        if (convertToCap) {
          value = value.toUpperCase ();
        }
        return null;
    }   
    ... ...
}

public class LoggedInInterceptor 
    {
   @AroundInvoke
   public Object checkLoggedIn(InvocationContext invocation) throws Exception 
   {   
      boolean isLoggedIn = Contexts.getSessionContext().get("loggedIn")!=null;
      if (isLoggedIn) {
         //the user is already logged in
         return invocation.proceed();
      }
      else {
         //the user is not logged in, fwd to login page
         return "login";
      }
   }
}

package.org.domain.MySeamProj.session;
import org.jboss.seam.annotations.In;
import org.jboss.seam.annotations.Logger;
import org.jboss.seam.annotations.Name;
import org.jboss.seam.log.Log;
import org.jboss.seam.security.identity;
    
    @Name ("authenticator")
    public class Authenticator
    {
      @Logger Log log;
      @In Identity identity;
      
      public boolean authenticate()
      {
        log.info("authenticating #0", identity.getUserName());
        //write your authentication logic here,
        //return true if the authentication was
        //successful, false otherwise
        identity.addRole("admin");
        return true;
        }
    }
